mapper.select:
select
sku_id, spu_id, sku_name, sku_desc, catalog_id, brand_id, sku_default_img, sku_title, sku_subtitle, price,
sale_count, user_id
from pms_sku_info


        and sku_id = #{skuId}


        and spu_id = #{spuId}


        and sku_name = #{skuName}


        and sku_desc = #{skuDesc}


        and catalog_id = #{catalogId}


        and brand_id = #{brandId}


        and sku_default_img = #{skuDefaultImg}


        and sku_title = #{skuTitle}


        and sku_subtitle = #{skuSubtitle}


        and price = #{price}


        and sale_count = #{saleCount}


        and sku_belong = #{skuBelong}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from pms_sku_info


        and sku_id = #{skuId}


        and spu_id = #{spuId}


        and sku_name = #{skuName}


        and sku_desc = #{skuDesc}


        and catalog_id = #{catalogId}


        and brand_id = #{brandId}


        and sku_default_img = #{skuDefaultImg}


        and sku_title = #{skuTitle}


        and sku_subtitle = #{skuSubtitle}


        and price = #{price}


        and sale_count = #{saleCount}


        and sku_belong = #{skuBelong}

select user_id, sku_name
from pms_sku_info
where sku_id = #{skuId}

 select pms_sku_info.* from pms_sku_info
left join pms_sku_sale_attr_value on pms_sku_info.sku_id = pms_sku_sale_attr_value.sku_id



           pms_sku_sale_attr_value.attr_id = #{attr.attrId}
           and
           pms_sku_sale_attr_value.attr_value = #{attr.attrValue}


 and  catalog_id = #{pamrs.catId} and price between #{pamrs.priceLow} and #{pamrs.priceHigh}


 limit #{pamrs.offset},#{pamrs.limit}

select * from pms_sku_info where catalog_id in (
    SELECT * FROM
        (select cat_ID from pms_recommend_product WHERE user_id=#{userId} GROUP BY cat_ID LIMIT 5) a
) and stock>0 limit #{offset},#{limit}
